    CR_HANDLER: function CR_HANDLER() {
      switch (this.$route.name) {
        case 'Income':
          return {
            emisor: 'responsable',
            receptor: 'client',
            type: 'income'
          };
          break;

        case 'Outcome':
          return {
            emisor: 'client',
            receptor: 'responsable',
            type: 'outcome'
          };
          break;

        default:
      }
    },
    INCOME_CLIENT: function INCOME_CLIENT() {
      if (this.getIncome[this.CR_HANDLER.receptor].type && this.getIncome[this.CR_HANDLER.receptor].type != null) {
        return this.getIncome[this.CR_HANDLER.receptor].data;
      } else {
        if (this.getIncome._id) {
          return this.getIncome[this.CR_HANDLER.receptor].name;
        } else {
          return this.getIncome.contactName;
        }
      }
    },
    INCOME_RESPONSABLE: function INCOME_RESPONSABLE() {
      if (this.getIncome[this.CR_HANDLER.emisor].type && this.getIncome[this.CR_HANDLER.emisor].type != null) {
        return this.getIncome[this.CR_HANDLER.emisor].data;
      } else {
        if (this.$route.params.id) {
          return this.getIncome[this.CR_HANDLER.emisor].name;
        } else {
          return this.getIncome.contactName;
        }
      }
    },
    INCOME: function INCOME() {
      return {
        _id: this.getIncome._id,
        name: this.getIncome.name,
        description: this.getIncome.description,
        dates: {
          expiration: null
        },
        creator: this.getIncome.creator,
        contactName: this.getIncome.client.name ? this.getIncome.client.name : null,
        client: this.getIncome[this.CR_HANDLER.receptor].type && this.getIncome[this.CR_HANDLER.receptor].type != null ? {
          type: this.getIncome[this.CR_HANDLER.receptor].type,
          data: this.INCOME_CLIENT
        } : {
          name: this.INCOME_CLIENT
        },
        responsable: this.getIncome[this.CR_HANDLER.emisor].type && this.getIncome[this.CR_HANDLER.emisor].type != null ? {
          type: this.getIncome[this.CR_HANDLER.emisor].type,
          data: this.INCOME_RESPONSABLE
        } : {
          name: this.INCOME_RESPONSABLE
        },
        // ONLY FOR POST REQUEST, IN GET REQUEST IM USING GETTER (this.getIncome)
        editor: this.CR_HANDLER.type == 'income' ? this.INCOME_RESPONSABLE : this.INCOME_CLIENT,
        lines: [],
        total: {
          net: 0,
          tax: 0
        },
        state: 'draft',
        isActive: true,
        currency: null
      };
    },
    OPTIONAL_USER: function OPTIONAL_USER() {
      return {
        name: this.CR_HANDLER.type == 'income' ? this.getIncome.client.name : this.getIncome.responsable.name,
        email: null
      };
    },
    CONTACT: function CONTACT() {
      return {
        creator: this.getContact.creator,
        email: this.getContact.email,
        isActive: true,
        name: this.getContact.name,
        phone: this.getContact.phone,
        user: this.getContact.user
      };
    },
    TOTAL: function TOTAL() {
      var total = _.sumBy(this.getIncome.lines, function (line) {
        return line.price * line.quantity;
      });

      return {
        net: total,
        tax: total * 0.19,
        total: total * 0.19 + total
      };
    }
  }),
  watch: {
    INCOME_CLIENT: function INCOME_CLIENT(val) {
      console.log('this.INCOME_CLIENT -> ', this.INCOME_CLIENT);
      console.log('this.INCOME_RESPONSABLE -> ', this.INCOME_RESPONSABLE);

      if (val) {
        if (!val.name) {
          this.acomplete.initialValue = val;
        } else {
          this.acomplete.initialValue = val.name;
        }
      }
    }
  },
  // NAVIGATIONS GUARDS
  beforeRouteEnter: function beforeRouteEnter(to, from, next) {
    store.commit('HIDE_SHOW_BOTTOM', false);
    store.commit('HIDE_SHOW_TOTALS', true);
    store.commit('HIDE_SHOW_NAVBAR', false);
    next();
  },
  beforeRouteLeave: function beforeRouteLeave(to, from, next) {
    store.commit('HIDE_SHOW_BOTTOM', true);
    store.commit('HIDE_SHOW_TOTALS', false);
    store.commit('HIDE_SHOW_NAVBAR', true);
    this.compareOnExit();
    next();
  }
};
